import 'package:flutter/material.dart';import 'package:instagram_clone/models/global_data.dart';import 'package:instagram_clone/ui/shared/ui_helpers.dart';import 'package:instagram_clone/ui/theme/app_text_styles.dart';import 'package:instagram_clone/ui/views/comments/comment_view_model.dart';import 'package:instagram_clone/ui/views/widgets/circle_progressbar.dart';import 'package:stacked/stacked.dart';class CommentView extends StatefulWidget {  @override  _CommentViewState createState() => _CommentViewState();}class _CommentViewState extends State<CommentView> {  @override  Widget build(BuildContext context) {    return ViewModelBuilder<CommentViewModel>.reactive(      builder: (context, model, child) => Scaffold(        appBar: AppBar(          title: Text('Comments'),        ),        body: _Body(),      ),      viewModelBuilder: () => CommentViewModel(),    );  }}class _Body extends ViewModelWidget<CommentViewModel> {  @override  Widget build(BuildContext context, CommentViewModel model) {    return model.isBusy        ? showCircleProgress()        : ListView.builder(            itemCount: GlobalData.commentDataModel?.length ?? 0,            itemBuilder: (context, index) => Padding(              padding: const EdgeInsets.all(8.0),              child: Row(                children: [                  CircleAvatar(                    radius: 16.0,                    backgroundImage:                        NetworkImage('https://picsum.photos/id/1005/100/100'),                    backgroundColor: Colors.transparent,                  ),                  horizontalSpaceSmall,                  RichText(                    text: TextSpan(                      style: AppTextStyles.secondaryTextStyle2(),                      children: <TextSpan>[                        TextSpan(                          text: GlobalData.commentDataModel[index]?.username ??                              '',                          style: AppTextStyles.secondaryTextStyle2()                              .copyWith(fontWeight: FontWeight.bold),                        ),                        TextSpan(                          text: '  ',                        ),                        TextSpan(                          text: GlobalData.commentDataModel[index]?.comments ??                              '',                          style: AppTextStyles.secondaryTextStyle2(),                        ),                      ],                    ),                  ),                ],              ),            ),          );  }}