import 'package:instagram_clone/app/locator.dart';import 'package:instagram_clone/models/comment_data_model.dart';import 'package:instagram_clone/models/global_data.dart';import 'package:instagram_clone/services/home_data_services.dart';import 'package:stacked/stacked.dart';import 'package:stacked_services/stacked_services.dart';class CommentViewModel extends FutureViewModel {  final HomeDataService homeDataService = locator<HomeDataService>();  final DialogService _dialogService = locator<DialogService>();  ///retrieving comments  Future getComments() async {    setBusy(true);    var result = await homeDataService.getComments();    setBusy(false);    if (result.statusCode == 200) {      GlobalData.commentDataModel = commentDataModelFromMap(result.data);      print('GlobalData.commentDataModel-----');      print(GlobalData.commentDataModel);    } else {      await _dialogService.showDialog(        title: 'Network Failure',        description: 'Something went wrong',      );    }  }  @override  Future futureToRun() => getComments();}