import 'package:flutter/material.dart';import 'package:instagram_clone/models/base_model.dart';class DashBoardModel extends BaseModel {  int selectedItemIndex = 0;  List<Map> bottomNavigation = [    {      'title': 'home',      'icon': Icon(Icons.home),      'outline': Icon(Icons.home_outlined)    },    {      'title': 'feed',      'icon': Icon(Icons.search),      'outline': Icon(Icons.search_outlined)    },    {      'title': 'feed',      'icon': Icon(Icons.add_circle),      'outline': Icon(Icons.add_circle_outline)    },    {      'title': 'feed',      'icon': Icon(Icons.favorite),      'outline': Icon(Icons.favorite_border_outlined)    },    {      'title': 'feed',      'icon': Icon(Icons.person),      'outline': Icon(Icons.person_outline)    },  ];  List get bottomNavList=>bottomNavigation;  int get selectedIndex=>selectedItemIndex;  ///onItemTapped  void onItemTapped(int index) {    selectedItemIndex=index;    notifyListeners();  }}