import 'package:flutter/material.dart';import 'package:instagram_clone/models/base_model.dart';import 'package:instagram_clone/ui/views/home/home_view.dart';class DashBoardModel extends BaseModel {  int selectedItemIndex = 0;  Widget selectedWidget = HomeView();  List<Map> bottomNavigation = [    {      'title': 'home',      'icon': Icon(Icons.home),      'outline': Icon(Icons.home_outlined)    },    {      'title': 'People',      'icon': Icon(Icons.search),      'outline': Icon(Icons.search_outlined)    },    {      'title': 'Feed',      'icon': Icon(Icons.add_circle),      'outline': Icon(Icons.add_circle_outline)    },    {      'title': 'Favorite',      'icon': Icon(Icons.favorite),      'outline': Icon(Icons.favorite_border_outlined)    },    {      'title': 'Bookmark',      'icon': Icon(Icons.bookmark),      'outline': Icon(Icons.bookmark_outline)    },  ];  List homWidgets = [    HomeView(),    Center(      child: Text(        'demo',      ),    ),    Center(      child: Text(        'demo',      ),    ),    Center(      child: Text(        'demo',      ),    ),    Container(        child: HomeView(      isBookMarked: true,    ))  ];  List get bottomNavList => bottomNavigation;  int get selectedIndex => selectedItemIndex;  Widget get getView => selectedWidget;  ///onItemTapped  void onItemTapped(int index) {    selectedItemIndex = index;    selectedWidget = homWidgets[index];    notifyListeners();  }}