import 'package:flutter/material.dart';import 'package:instagram_clone/ui/views/home/home_view_body.dart';import 'package:instagram_clone/ui/views/home/home_view_model.dart';import 'package:instagram_clone/ui/views/widgets/app_bar.dart';import 'package:stacked/stacked.dart';class HomeView extends StatefulWidget {  @override  _HomeViewState createState() => _HomeViewState();}class _HomeViewState extends State<HomeView> {  @override  void initState() {    HomeViewModel().getHomeData();    super.initState();  }  @override  Widget build(BuildContext context) {    return ViewModelBuilder<HomeViewModel>.reactive(      builder: (context, model, child) => Scaffold(        appBar: HomeAppBar(),        body: HomeBody(),      ),      viewModelBuilder: () => HomeViewModel(),    );  }}