import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:instagram_clone/ui/theme/app_text_styles.dart';import 'package:instagram_clone/ui/views/startup/start_up_view_model.dart';import 'package:stacked/stacked.dart';class StartUpView extends StatefulWidget {  @override  _StartUpViewState createState() => _StartUpViewState();}class _StartUpViewState extends State<StartUpView> {  @override  Widget build(BuildContext context) {    ScreenUtil.init(context,        designSize: Size(375, 812), allowFontScaling: false);    return ViewModelBuilder<StartUpViewModel>.reactive(      builder: (context, model, child) => Scaffold(        body: _Body(),      ),      viewModelBuilder: () => StartUpViewModel(),    );  }}class _Body extends ViewModelWidget<StartUpViewModel> {  @override  Widget build(BuildContext context, StartUpViewModel model) {    return Center(      child: Text(        'Instagram',        style: AppTextStyles.splashTextStyle(),        textAlign: TextAlign.center,      ),    );  }}