import 'package:instagram_clone/app/locator.dart';import 'package:instagram_clone/app/router.gr.dart';import 'package:stacked/stacked.dart';import 'package:stacked_services/stacked_services.dart';class StartUpViewModel extends FutureViewModel {  final NavigationService _navigationService = locator<NavigationService>();  Future<void> runSplashService() async {    await Future.delayed(const Duration(seconds: 3));    _navigationService.replaceWith(Routes.dashBoard);  }  @override  Future<void> futureToRun() => runSplashService();}