import 'package:flutter/material.dart';import 'package:instagram_clone/ui/theme/app_text_styles.dart';import 'package:instagram_clone/ui/theme/palette.dart';import 'package:instagram_clone/ui/views/home/home_view_model.dart';import 'package:stacked/stacked.dart';class HomeAppBar extends ViewModelWidget<HomeViewModel>    implements PreferredSizeWidget {  @override  Widget build(BuildContext context, HomeViewModel viewModel) {    return AppBar(      backgroundColor: Palette.backgroundColor,      elevation: .5,      title: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Icon(Icons.add_circle_outline,color: Colors.black,),          Text(            viewModel.title,            style: AppTextStyles.headerTextStyle().copyWith(                fontFamily: 'GrandHotel',                fontSize: 35,                fontWeight: FontWeight.normal),          ),          Image.asset('assets/send.png',height: 25,width: 25,)        ],      ),    );  }  @override  Size get preferredSize => const Size.fromHeight(60);}